mapper.select:
select a.id, a.user_id, a.category_id, a.title, a.content, a.view_num, a.comment_num, a.created_at, a.updated_at, u.nickname, c.name as category_name from h_article as a inner join h_user as u on a.user_id = u.id inner join h_category as c on a.category_id=c.id
where a.user_id=#{userId,jdbcType=INTEGER}
and a.category_id=#{categoryId,jdbcType=INTEGER}
order by a.created_at desc limit ${(pageNum - 1) * pageSize},${pageSize}

select count(id) from h_article

    where user_id=#{userId,jdbcType=INTEGER}


    and category_id=#{categoryId,jdbcType=INTEGER}

mapper.update:
update h_article set
category_id = #{categoryId,jdbcType=INTEGER},
title = #{title,jdbcType=VARCHAR},
content = #{content,jdbcType=LONGVARCHAR},
updated_at=#{updatedAt,jdbcType=INTEGER}
where id=#{id,jdbcType=INTEGER}
